// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../../verifiers/interfaces/ITxVerifier.sol";

interface IBrevisUniNFT {
    function mint(address to, uint256 id) external;
}

contract UniswapVolume is Ownable {
    ITxVerifier public txVerifier;

    mapping(uint64 => address) public universalRouter;

    mapping(address => uint256) public swapAmount;

    enum TierName {
        Stone,
        Bronze,
        Silver,
        Gold,
        Platinum,
        Diamond
    }
    mapping(TierName => uint256) public tierAmounts;
    mapping(TierName => address) public tierNFTs;
    mapping(TierName => uint256) public tierNextId;

    event VerifiedUniswapAmount(uint64 chainId, address trader, uint64 timestamp, uint256 amount);

    constructor(ITxVerifier _txVerifier) {
        txVerifier = _txVerifier;
    }

    function submitUniswapTxProof(
        bytes calldata _tx,
        bytes calldata _proof,
        bytes calldata _auxiBlkVerifyInfo,
        TierName tier
    ) external {
        ITxVerifier.TxInfo memory txInfo = txVerifier.verifyTx(_tx, _proof, _auxiBlkVerifyInfo);
        require(txInfo.to != address(0), "invalid to address");
        require(txInfo.from == msg.sender, "invalid from address");
        require(swapAmount[txInfo.from] == 0, "swap already proved for this user");

        uint256 amount;
        if (txInfo.to == universalRouter[txInfo.chainId]) {
            amount = decodeSwapInfo(txInfo.data);
        } else {
            revert("not tx to universalRouter");
        }
        swapAmount[txInfo.from] = amount;
        require(amount >= tierAmounts[tier], "wrong tier");
        IBrevisUniNFT(tierNFTs[tier]).mint(txInfo.from, tierNextId[tier]);
        tierNextId[tier] += 1;

        emit VerifiedUniswapAmount(txInfo.chainId, txInfo.from, txInfo.blkTime, amount);
    }

    function decodeSwapInfo(bytes memory _data) private pure returns (uint256 amount) {
        // TODO
    }

    function setUniversalRouter(uint64 _chainId, address _router) external onlyOwner {
        universalRouter[_chainId] = _router;
    }

    function setTierAmounts(TierName[] calldata _names, uint256[] calldata _amounts) external onlyOwner {
        require(_names.length == _amounts.length, "length mismatch");
        for (uint256 i = 0; i < _names.length; i++) {
            tierAmounts[_names[i]] = _amounts[i];
        }
    }

    function setTierNFTs(TierName[] calldata _names, address[] calldata _nfts) external onlyOwner {
        require(_names.length == _nfts.length, "length mismatch");
        for (uint256 i = 0; i < _names.length; i++) {
            tierNFTs[_names[i]] = _nfts[i];
        }
    }
}

contracts/chunk-sync/interfaces/IBlockCh.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

interface IBlockChunks {
    // historicalRoots(chainId, startBlockNumber) is 0 unless (startBlockNumber % 128 == 0)
    // historicalRoots(chainId, startBlockNumber) holds the hash of
    //   prevHash || root || numFinal
    // where
    // - prevHash is the parent hash of block startBlockNumber
    // - root is the partial Merkle root of blockhashes of block numbers
    //   [startBlockNumber, startBlockNumber + 128)
    //   where unconfirmed block hashes are 0's
    // - numFinal is the number of confirmed consecutive roots in [startBlockNumber, startBlockNumber + 128)
    function historicalRoots(uint64 chainId, uint32 startBlockNumber) external view returns (bytes32);

    event UpdateEvent(uint64 chainId, uint32 startBlockNumber, bytes32 prevHash, bytes32 root, uint32 numFinal);

    struct BlockHashWitness {
        uint64 chainId;
        uint32 blkNum;
        bytes32 claimedBlkHash;
        bytes32 prevHash;
        uint32 numFinal;
        bytes32[7] merkleProof;
    }

    // update blocks in the "backward" direction, anchoring on a "recent" end blockhash from anchor contract
    // * startBlockNumber must be a multiple of 128
    // * for now always endBlockNumber = startBlockNumber + 127 (full update on every 128 blocks chunk)
    function updateRecent(uint64 chainId, bytes calldata proofData) external;

    // update older blocks in "backwards" direction, anchoring on more recent trusted blockhash
    // must be batch of 128 blocks
    function updateOld(uint64 chainId, bytes32 nextRoot, uint32 nextNumFinal, bytes calldata proofData) external;

    function isBlockHashValid(BlockHashWitness calldata witness) external view returns (bool);
}

