            [
                uint256(3106477081800487684702197609336793578996599722738758416741894108250584952412),
                uint256(17769315574634517198959944866792285196528619991510833533647042528867740841497)
            ],
            [
                uint256(17550976402938237477690329891427538913705970488483325143361818027802831969580),
                uint256(19564919261896948583062923173775725062171897993601375119831785459533152098560)
            ]
        );
        vk.delta2 = Pairing.G2Point(
            [
                uint256(12892811443621733648975173907437618319776140089825570542949912397946888153254),
                uint256(4329993878235330304234158522137296465637295347745243858920373696586654891645)
            ],
            [
                uint256(13919225177395245875350213490350160393023488096741711658832974485762210768745),
                uint256(15219587570363605524638651955941153453620758942698393737137577829587158276942)
            ]
        );
    }

    // accumulate scalarMul(mul_input) into q
    // that is computes sets q = (mul_input[0:2] * mul_input[3]) + q
    function accumulate(
        uint256[3] memory mul_input,
        Pairing.G1Point memory p,
        uint256[4] memory buffer,
        Pairing.G1Point memory q
    ) internal view {
        // computes p = mul_input[0:2] * mul_input[3]
        Pairing.scalar_mul_raw(mul_input, p);

        // point addition inputs
        buffer[0] = q.X;
        buffer[1] = q.Y;
        buffer[2] = p.X;
        buffer[3] = p.Y;

        // q = p + q
        Pairing.plus_raw(buffer, q);
    }

    /*
     * @returns Whether the proof is valid given the hardcoded verifying key
     *          above and the public inputs
     */
    function verifyProof(
        uint256[2] memory a,
        uint256[2][2] memory b,
        uint256[2] memory c,
        uint256[2] memory commit,
        uint256[6] calldata input
    ) public view returns (bool r) {
        Proof memory proof;
        proof.A = Pairing.G1Point(a[0], a[1]);
        proof.B = Pairing.G2Point([b[0][0], b[0][1]], [b[1][0], b[1][1]]);
        proof.C = Pairing.G1Point(c[0], c[1]);
        proof.Commit = Pairing.G1Point(commit[0], commit[1]);

        // Make sure that proof.A, B, and C are each less than the prime q
        require(proof.A.X < PRIME_Q, "verifier-aX-gte-prime-q");
        require(proof.A.Y < PRIME_Q, "verifier-aY-gte-prime-q");

        require(proof.B.X[0] < PRIME_Q, "verifier-bX0-gte-prime-q");
        require(proof.B.Y[0] < PRIME_Q, "verifier-bY0-gte-prime-q");

        require(proof.B.X[1] < PRIME_Q, "verifier-bX1-gte-prime-q");
        require(proof.B.Y[1] < PRIME_Q, "verifier-bY1-gte-prime-q");

        require(proof.C.X < PRIME_Q, "verifier-cX-gte-prime-q");
        require(proof.C.Y < PRIME_Q, "verifier-cY-gte-prime-q");

        // Make sure that every input is less than the snark scalar field
        for (uint256 i = 0; i < input.length; i++) {
            require(input[i] < SNARK_SCALAR_FIELD, "verifier-gte-snark-scalar-field");
        }

        VerifyingKey memory vk = verifyingKey();

        // Compute the linear combination vk_x
        Pairing.G1Point memory vk_x = Pairing.G1Point(0, 0);

        // Buffer reused for addition p1 + p2 to avoid memory allocations
        // [0:2] -> p1.X, p1.Y ; [2:4] -> p2.X, p2.Y
        uint256[4] memory add_input;

        // Buffer reused for multiplication p1 * s
        // [0:2] -> p1.X, p1.Y ; [3] -> s
        uint256[3] memory mul_input;

        // temporary point to avoid extra allocations in accumulate
        Pairing.G1Point memory q = Pairing.G1Point(0, 0);

        vk_x.X = uint256(11966620651374967254643410260589552442195768077678643408414034660856248063082); // vk.K[0].X
        vk_x.Y = uint256(14201972430997316666699324027320825798497165005985605117136793951446085331090); // vk.K[0].Y
        mul_input[0] = uint256(9600558441001135400505290048756646232880516973895317897389325670316519836157); // vk.K[1].X
        mul_input[1] = uint256(16760364464242344172069241426179100713735802313271688691105188634950125317453); // vk.K[1].Y
        mul_input[2] = input[0];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[1] * input[0]
        mul_input[0] = uint256(3740034587536093064133894334154113883056085690271209650376798101138920054248); // vk.K[2].X
        mul_input[1] = uint256(11246497375627702638239696598461496801899600113304086248719493779002935317755); // vk.K[2].Y
        mul_input[2] = input[1];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[2] * input[1]
        mul_input[0] = uint256(13030229328455313863332076106877975874541332406100355032047773418791826960353); // vk.K[3].X
        mul_input[1] = uint256(9733066468969270460191473800945221923631880026974745671838901176243676185175); // vk.K[3].Y
        mul_input[2] = input[2];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[3] * input[2]
        mul_input[0] = uint256(6189471970846665438232841261308948563909763145001276487103946573140541149139); // vk.K[4].X
        mul_input[1] = uint256(10715572537296481203145410189810035243350069028404350348388136012166304031571); // vk.K[4].Y
        mul_input[2] = input[3];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[4] * input[3]
        mul_input[0] = uint256(17523712979343547665011752293906977166261741147452247522372423266208174046122); // vk.K[5].X
        mul_input[1] = uint256(3551271029987179661786623970827871391238564096356098036215143026253700157271); // vk.K[5].Y
        mul_input[2] = input[4];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[5] * input[4]
        mul_input[0] = uint256(12796423093885476905323875458445033293296377846238370196878216696080485113559); // vk.K[6].X
        mul_input[1] = uint256(12127470002424275366470792560424768578355558242766007939973968221501051346300); // vk.K[6].Y
        mul_input[2] = input[5];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[6] * input[5]
        if (commit[0] != 0 || commit[1] != 0) {
            vk_x = Pairing.plus(vk_x, proof.Commit);
        }

        return
            Pairing.pairing(Pairing.negate(proof.A), proof.B, vk.alfa1, vk.beta2, vk_x, vk.gamma2, proof.C, vk.delta2);
    }
}

