pragma solidity ^0.8.0;

import "./common/Pairing.sol";
import "./common/Constants.sol";
import "./common/Common.sol";

contract EthStorageVerifier {
    using Pairing for *;

    struct VerifyingKey {
        Pairing.G1Point alfa1;
        Pairing.G2Point beta2;
        Pairing.G2Point gamma2;
        Pairing.G2Point delta2;
        // []G1Point IC (K in gnark) appears directly in verifyProof
    }

    struct Proof {
        Pairing.G1Point A;
        Pairing.G2Point B;
        Pairing.G1Point C;
        Pairing.G1Point Commit;
    }

    function verifyingKey() internal pure returns (VerifyingKey memory vk) {
        vk.alfa1 = Pairing.G1Point(
            uint256(4101194415195068025423874713299757109624368409561482108170376832586679531405),
            uint256(4258395440362667126312935482794695741222608482150653359018346434337602508946)
        );
        vk.beta2 = Pairing.G2Point(
            [
                uint256(16439514875033274687454048461377996745326195782655333965576073480836691883559),
                uint256(19276203858306240750277475695807151297248730490961797886027456650995005321959)
            ],
            [
                uint256(19060953054949632557505353041350268914431326836117845728665299546912330812818),
                uint256(4698702276323878506769802352286111743331920171021274587560571984285702023928)
            ]
        );
        vk.gamma2 = Pairing.G2Point(
            [
                uint256(12838461299153070211771740521976432485177653380836211615829770099493232139064),
                uint256(21183238866260352420957994792199103433633945560940750430485497773712222687210)
            ],
            [
                uint256(17833421956262291594989392582813776941326904872094028568146525236797053854992),
                uint256(16870220658894912225499336011210296770804995380996232515561786091840591541590)
            ]
        );
        vk.delta2 = Pairing.G2Point(
            [
                uint256(9656699309964346278718040058127642112594472223235587127181580233464527618277),
                uint256(19823064266563161392144329690471889204117741805048436633177469904580008915304)
            ],
            [
                uint256(1997570263637684074581409993197259576695125905630672663128757086882404277806),
                uint256(21296156718838357686129692666693528046179496134366711708929536738920590035238)
            ]
        );
    }

    // accumulate scalarMul(mul_input) into q
    // that is computes sets q = (mul_input[0:2] * mul_input[3]) + q
    function accumulate(
        uint256[3] memory mul_input,
        Pairing.G1Point memory p,
        uint256[4] memory buffer,
        Pairing.G1Point memory q
    ) internal view {
        // computes p = mul_input[0:2] * mul_input[3]
        Pairing.scalar_mul_raw(mul_input, p);

        // point addition inputs
        buffer[0] = q.X;
        buffer[1] = q.Y;
        buffer[2] = p.X;
        buffer[3] = p.Y;

        // q = p + q
        Pairing.plus_raw(buffer, q);
    }

    /*
     * @returns Whether the proof is valid given the hardcoded verifying key
     *          above and the public inputs
     */
    function verifyProof(
        uint256[2] memory a,
        uint256[2][2] memory b,
        uint256[2] memory c,
        uint256[2] memory commit,
        uint256[9] memory input
    ) public view returns (bool r) {
        Proof memory proof;
        proof.A = Pairing.G1Point(a[0], a[1]);
        proof.B = Pairing.G2Point([b[0][0], b[0][1]], [b[1][0], b[1][1]]);
        proof.C = Pairing.G1Point(c[0], c[1]);
        proof.Commit = Pairing.G1Point(commit[0], commit[1]);

        // Make sure that proof.A, B, and C are each less than the prime q
        require(proof.A.X < PRIME_Q, "verifier-aX-gte-prime-q");
        require(proof.A.Y < PRIME_Q, "verifier-aY-gte-prime-q");

        require(proof.B.X[0] < PRIME_Q, "verifier-bX0-gte-prime-q");
        require(proof.B.Y[0] < PRIME_Q, "verifier-bY0-gte-prime-q");

        require(proof.B.X[1] < PRIME_Q, "verifier-bX1-gte-prime-q");
        require(proof.B.Y[1] < PRIME_Q, "verifier-bY1-gte-prime-q");

        require(proof.C.X < PRIME_Q, "verifier-cX-gte-prime-q");
        require(proof.C.Y < PRIME_Q, "verifier-cY-gte-prime-q");

        // Make sure that every input is less than the snark scalar field
        for (uint256 i = 0; i < input.length; i++) {
            require(input[i] < SNARK_SCALAR_FIELD, "verifier-gte-snark-scalar-field");
        }

        VerifyingKey memory vk = verifyingKey();

        // Compute the linear combination vk_x
        Pairing.G1Point memory vk_x = Pairing.G1Point(0, 0);

        // Buffer reused for addition p1 + p2 to avoid memory allocations
        // [0:2] -> p1.X, p1.Y ; [2:4] -> p2.X, p2.Y
        uint256[4] memory add_input;

        // Buffer reused for multiplication p1 * s
        // [0:2] -> p1.X, p1.Y ; [3] -> s
        uint256[3] memory mul_input;

        // temporary point to avoid extra allocations in accumulate
        Pairing.G1Point memory q = Pairing.G1Point(0, 0);

        vk_x.X = uint256(11324798991801455719954252063583334670536437952341509943176836321388782785475); // vk.K[0].X
        vk_x.Y = uint256(12326427617847987634187499233539079835136211843857405454969210436632570846644); // vk.K[0].Y
        mul_input[0] = uint256(15855758052910946362564301207345214800941948855526929671171992550648923901194); // vk.K[1].X
        mul_input[1] = uint256(8814837402073387239890142132996274060688382484773297478886074148806745320694); // vk.K[1].Y
        mul_input[2] = input[0];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[1] * input[0]
        mul_input[0] = uint256(6522741885788032953156164277068652456095530543849390614185398653638226383320); // vk.K[2].X
        mul_input[1] = uint256(21192739319070000135810363152030394321048757677557147623013216697480179276271); // vk.K[2].Y
        mul_input[2] = input[1];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[2] * input[1]
        mul_input[0] = uint256(11958951098730554082216072825282969421721647367014815269992686619331778317908); // vk.K[3].X
        mul_input[1] = uint256(10655457940451391962242848166798429511880151597239431088751286306299649577129); // vk.K[3].Y
        mul_input[2] = input[2];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[3] * input[2]
        mul_input[0] = uint256(17186075274510691052239294402855156934324874491974465566959055818508971850332); // vk.K[4].X
        mul_input[1] = uint256(19158069234039211539526983551792889121978716641772966009217546796359790957076); // vk.K[4].Y
        mul_input[2] = input[3];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[4] * input[3]
        mul_input[0] = uint256(9714094901466418966170048415820685381424848766104412220565399689260664652072); // vk.K[5].X
        mul_input[1] = uint256(2249856808164293755600652581653693354796803855461791739554458217477761697306); // vk.K[5].Y
        mul_input[2] = input[4];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[5] * input[4]
        mul_input[0] = uint256(17224884247093246510191903557819707122090369916854086297176718409151948017398); // vk.K[6].X
        mul_input[1] = uint256(528501653263386182780162102397973366413989345917948755563786102759128317402); // vk.K[6].Y
        mul_input[2] = input[5];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[6] * input[5]
        mul_input[0] = uint256(361174086899201465184609656237690432862909345413059635441144532044010353899); // vk.K[7].X
        mul_input[1] = uint256(13366727442090128273019302009232654673396906988981409672802134248099590073825); // vk.K[7].Y
        mul_input[2] = input[6];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[7] * input[6]
        mul_input[0] = uint256(18653179265997504904500924363864280893939557576747047870034827610218133388062); // vk.K[8].X
        mul_input[1] = uint256(2414988480708584541105466146832501629915521197512569819894185302890984806761); // vk.K[8].Y
        mul_input[2] = input[7];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[8] * input[7]
        mul_input[0] = uint256(20817613062917486844360076494632614720655961007368164725678903685604190477822); // vk.K[9].X
        mul_input[1] = uint256(4044560346597429541255539481561957938362798150817993714804018870646190181781); // vk.K[9].Y
        mul_input[2] = input[8];
        accumulate(mul_input, q, add_input, vk_x); // vk_x += vk.K[9] * input[8]
        if (commit[0] != 0 || commit[1] != 0) {
            vk_x = Pairing.plus(vk_x, proof.Commit);
        }

        return
            Pairing.pairing(Pairing.negate(proof.A), proof.B, vk.alfa1, vk.beta2, vk_x, vk.gamma2, proof.C, vk.delta2);
    }

    function verifyRaw(bytes calldata proofData) external view returns (bool) {
        uint256[2] memory a;
        a[0] = uint256(bytes32(proofData[:32]));
        a[1] = uint256(bytes32(proofData[32:64]));
        uint256[2][2] memory b;
        b[0][0] = uint256(bytes32(proofData[64:96]));
        b[0][1] = uint256(bytes32(proofData[96:128]));
        b[1][0] = uint256(bytes32(proofData[128:160]));
        b[1][1] = uint256(bytes32(proofData[160:192]));
        uint256[2] memory c;
        c[0] = uint256(bytes32(proofData[192:224]));
        c[1] = uint256(bytes32(proofData[224:256]));
        uint256[2] memory commit;
        commit[0] = uint256(bytes32(proofData[256:288]));
        commit[1] = uint256(bytes32(proofData[288:320]));
        uint256[9] memory input;
        input[0] = uint256(bytes32(proofData[320:352]));
        input[1] = uint256(bytes32(proofData[352:384]));
        input[2] = uint256(bytes32(proofData[384:416]));
        input[3] = uint256(bytes32(proofData[416:448]));
        input[4] = uint256(bytes32(proofData[448:480]));
        input[5] = uint256(bytes32(proofData[480:512]));
        input[6] = uint256(bytes32(proofData[512:544]));
        input[7] = uint256(bytes32(proofData[544:576]));
        input[8] = uint256(bytes32(proofData[576:608]));

        return verifyProof(a, b, c, commit, input);
    }
}
