contracts/apps/receipt-demo/VerifyEmitNu.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../../verifiers/interfaces/IReceiptVerifier.sol";
import {BytesLib} from "solidity-bytes-utils/contracts/BytesLib.sol";

contract VerifyEmitNumber is Ownable {
    IReceiptVerifier public receiptVerifier;

    event VerifiedNumber(uint64 chainId, uint64 blknum, address from, uint256 number);

    constructor(IReceiptVerifier _receiptVerifier) {
        receiptVerifier = _receiptVerifier;
    }

    mapping(uint64 => address) public srcContract;

    bytes32 private logTopic = keccak256(bytes("SendNumber(address,uint256)"));

    function submitNumberReceiptProof(
        bytes calldata _receipt,
        bytes calldata _proof,
        bytes calldata _auxiBlkVerifyInfo) external {
        IReceiptVerifier.ReceiptInfo memory receiptInfo = receiptVerifier.verifyReceiptAndLog(_receipt, _proof, _auxiBlkVerifyInfo);
        // status must be 1
        require(bytes1(receiptInfo.status) == 0x01, "receipt status is fail");
        address sc = srcContract[receiptInfo.chainId];
        bool findLog;
        address from;
        uint256 number;

        // loop to get the target event
        // TODO, for developer, log index can be a arg, avoid useless for loop
        for (uint256 i = 0; i < receiptInfo.logs.length; i++) {
            if (receiptInfo.logs[i].addr == sc && receiptInfo.logs[i].topics[0] == logTopic) {
                from = address(bytes20(BytesLib.slice(receiptInfo.logs[i].data, 12, 32)));
                number = uint256(bytes32(BytesLib.slice(receiptInfo.logs[i].data, 32, 64)));
                findLog = true;
                break;
            }
        }
        require(findLog == true, "fail to find the event log");
        emit VerifiedNumber(receiptInfo.chainId, receiptInfo.blkNum, from, number);
    }

    function setReceiptVerifier(IReceiptVerifier _receiptVerifier) external onlyOwner {
        receiptVerifier = _receiptVerifier;
    }

    function setSrcContract(uint64 _chainId, address _contract) external onlyOwner {
        srcContract[_chainId] = _contract;
    }
}

contracts/apps/slot-value-demo/MsgProof..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "../../verifiers/interfaces/ISlotValueVerifier.sol";

// source chain contract
contract MsgSender {
    uint64 public nonce; // slot 0
    mapping(uint64 => bytes32) public sent; // slot 1, nonce -> msgHash
    event MsgSent(uint64 nonce, address sender, bytes msg);

    function sendMsg(bytes calldata _msg) external {
        bytes32 msgHash = keccak256(abi.encodePacked(msg.sender, _msg));
        sent[nonce] = msgHash; // store in slot keccak256(abi.encode(_nonce, 1))
        emit MsgSent(nonce++, msg.sender, _msg);
    }
}

// destination chain contract
contract MsgReceiver {
    uint64 public senderChainId;
    bytes32 public senderContractHash;
    ISlotValueVerifier public slotValueVerifier;

    event MsgReceived(uint64 nonce, address sender, bytes msg);

    constructor(ISlotValueVerifier _verifier, uint64 _senderChainId, address _senderContract) {
        slotValueVerifier = _verifier;
        senderChainId = _senderChainId;
        senderContractHash = keccak256(abi.encodePacked(_senderContract));
    }

    function recvMsg(
        uint64 _nonce,
        address _sender,
        bytes calldata _msg,
        bytes calldata _proofData,
        bytes calldata _blkVerifyInfo
    ) external {
        // compute expected slot and msg hash, sender map slot is 1
        bytes32 slotKeyHash = keccak256(abi.encode(keccak256(abi.encode(_nonce, 1))));
        bytes32 msgHash = keccak256(abi.encodePacked(_sender, _msg));
        // retrieve zk verified slot info
        ISlotValueVerifier.SlotInfo memory slotInfo = slotValueVerifier.verifySlotValue(
            senderChainId,
            _proofData,
            _blkVerifyInfo
        );
        // compare expected and verified values
        require(slotInfo.slotKeyHash == slotKeyHash, "slot key not match");
        require(slotInfo.slotValue == msgHash, "slot value not match");
        require(slotInfo.addrHash == senderContractHash, "sender contract not match");
        emit MsgReceived(_nonce, _sender, _msg);
    }
}

contracts/apps/slot-value-demo/SlotValue.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "../../verifiers/interfaces/ISlotValueVerifier.sol";

contract SlotValue {
    ISlotValueVerifier public slotValueVerifier;

    event VerifiedSlotValueProof(
        uint64 chainId,
        bytes32 addrHash,
        bytes32 slotKeyHash,
        bytes32 slotValue,
        uint32 blkNum,
        bytes32 blkHash
    );

    constructor(ISlotValueVerifier _verifier) {
        slotValueVerifier = _verifier;
    }

    function submitSlotValuePoof(uint64 chainId, bytes calldata proofData, bytes calldata blkVerifyInfo) external {
        ISlotValueVerifier.SlotInfo memory slotInfo = slotValueVerifier.verifySlotValue(
            chainId,
            proofData,
            blkVerifyInfo
        );
        emit VerifiedSlotValueProof(
            slotInfo.chainId,
            slotInfo.addrHash,
            slotInfo.slotKeyHash,
            slotInfo.slotValue,
            slotInfo.blkNum,
            slotInfo.blkHash
        );
    }
}

