        uint256[6] memory input
    ) private {
        bool success = IProofVerifier(verifier).verifyProof(a, b, c, commit, input);
        emit ProofVerified(success);
    }

    function verifyProof(
        uint256[2] memory a,
        uint256[2][2] memory b,
        uint256[2] memory c,
        uint256[2] memory commit,
